-- LSPD Definitions
local ListLSPDLists = {LSPDTops, LSPDLegs, LSPDAccessoires}

for n, List in ipairs(ListLSPDLists) do
  for i, Cloth in ipairs(List) do
    if Cloth.Type == 0 then

      if Cloth.ComponentID == 6 then

        local CategoryIndex = 0
        if ListLSPDComponents[Cloth.ComponentID].Categories[Cloth.CategoryIndex] == nil then
          ListLSPDComponents[Cloth.ComponentID].Categories[Cloth.CategoryIndex] = {}
        else
          for j, Category in ipairs(ListLSPDComponents[Cloth.ComponentID].Categories[Cloth.CategoryIndex]) do
            if Category == Cloth.Category then
              CategoryIndex = j
              break
            end
          end
        end
        if CategoryIndex == 0 then
          table.insert(ListLSPDComponents[Cloth.ComponentID].Categories[Cloth.CategoryIndex], Cloth.Category)
          ListLSPDComponents[Cloth.ComponentID].Items[Cloth.CategoryIndex] = {}
          ListLSPDComponents[Cloth.ComponentID].Options[Cloth.CategoryIndex] = {}
          table.insert(ListLSPDComponents[Cloth.ComponentID].Items[Cloth.CategoryIndex], {Cloth.Name})
          table.insert(ListLSPDComponents[Cloth.ComponentID].Options[Cloth.CategoryIndex], { { {Cloth.Male, Cloth.Female} } })
        else
          local ItemIndex = 0
          if ListLSPDComponents[Cloth.ComponentID].Items[Cloth.CategoryIndex][CategoryIndex] == nil then
            ListLSPDComponents[Cloth.ComponentID].Items[Cloth.CategoryIndex][CategoryIndex] = {}
            ListLSPDComponents[Cloth.ComponentID].Options[Cloth.CategoryIndex][CategoryIndex] = {}
          else
            for k, Item in ipairs(ListLSPDComponents[Cloth.ComponentID].Items[Cloth.CategoryIndex][CategoryIndex]) do
              if Item == Cloth.Name then
                ItemIndex = k
                break
              end
            end
          end
          if ItemIndex == 0 then
            table.insert(ListLSPDComponents[Cloth.ComponentID].Items[Cloth.CategoryIndex][CategoryIndex], Cloth.Name)
            table.insert(ListLSPDComponents[Cloth.ComponentID].Options[Cloth.CategoryIndex][CategoryIndex], { {Cloth.Male, Cloth.Female} })
          else
            table.insert(ListLSPDComponents[Cloth.ComponentID].Options[Cloth.CategoryIndex][CategoryIndex][ItemIndex], {Cloth.Male, Cloth.Female} )
          end
        end

      elseif Cloth.ComponentID == 8 or Cloth.ComponentID == 9 then

        local CategoryIndex = 0
        if ListLSPDComponents[Cloth.ComponentID].Categories[Cloth.CategoryIndex] == nil then
          ListLSPDComponents[Cloth.ComponentID].Categories[Cloth.CategoryIndex] = {}
